// src/pages/Home.js
import React from 'react';
import './Home.css';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import Typed from 'react-typed'; // Import react-typed library
import Gallery from '../pages/Gallery';
import SMK from '../images/smk4.jpg';
import UNIV from '../images/upi.jpeg';

function Home() {
  const itemsGallery = [
    {
      image: 'https://via.placeholder.com/200',
      alt: 'Project 1',
      caption: 'Project 1 - Description',
    },
    {
      image: 'https://via.placeholder.com/200',
      alt: 'Project 2',
      caption: 'Project 2 - Description',
    },
    {
      image: 'https://via.placeholder.com/200',
      alt: 'Project 3',
      caption: 'Project 3 - Description',
    },
    // Add more gallery items as needed
  ];

  const itemsCarousel = [
    {
      image: SMK,
      title: 'SMKN 4 Payakumbuh',
      description: "My 'home' in 2019-2022",
    },
    {
      image: UNIV,
      title: 'Universitas Pendidikan Indonesia',
      description: "My beloved 'home' at the moment",
    },
    // Add more carousel items as needed
  ];

  return (
    <div className="page">
      {/* Komponen Typed dengan animasi teks yang diketik */}
      <Typed
        strings={['Welcome to My Portfolio!', 'I Am Azkal Azkiya Alfiandri', 'Connect with me.']}
        typeSpeed={30}
        backSpeed={30}
        backDelay={1500}
        showCursor
        cursorChar="|"
        className="typed-text"
        style={{
          display: 'block', padding: '5rem', backgroundImage: 'linear-gradient(to bottom, #4F6F52, #92ad91)', 
          margin: '0', fontSize:'60px', color: '#ECE3CE',
        }}
      />

      <Gallery itemsGallery={itemsGallery} itemsCarousel={itemsCarousel} />

    </div>
  );
}

export default Home;
