// src/pages/Home.js
import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import { Carousel } from 'react-bootstrap';
import { useSpring, animated } from 'react-spring';

function Home() {
  const carouselSpring = useSpring({
    from: { opacity: 0 },
    to: { opacity: 1 },
    config: { duration: 1000 },
  });

  const [typedText, setTypedText] = useState('');
  const targetText = 'Welcome to My Portfolio!';

  useEffect(() => {
    const timeout = setTimeout(() => {
      setTypedText((prevText) =>
        prevText.length === targetText.length
          ? prevText
          : targetText.slice(0, prevText.length + 1)
      );
    }, 100);

    return () => clearTimeout(timeout);
  }, [typedText, targetText]);

  return (
    <div className="page">
      <animated.div
        style={{ ...carouselSpring, backgroundColor: '#f0f0f0', margin: '2px 0' }}
        className="jumbotron text-center text-white p-4"
      >
        <h1 className="text-4xl">{typedText}</h1>
      </animated.div>

      <Carousel>
        <Carousel.Item>
          <img
            className="d-block w-100"
            src="https://via.placeholder.com/800x400"
            alt="First slide"
          />
          <Carousel.Caption>
            <h3>First Slide</h3>
            <p>Some text about the first slide.</p>
          </Carousel.Caption>
        </Carousel.Item>

        <Carousel.Item>
          <img
            className="d-block w-100"
            src="https://via.placeholder.com/800x400"
            alt="Second slide"
          />
          <Carousel.Caption>
            <h3>Second Slide</h3>
            <p>Some text about the second slide.</p>
          </Carousel.Caption>
        </Carousel.Item>

        <Carousel.Item>
          <img
            className="d-block w-100"
            src="https://via.placeholder.com/800x400"
            alt="Third slide"
          />
          <Carousel.Caption>
            <h3>Third Slide</h3>
            <p>Some text about the third slide.</p>
          </Carousel.Caption>
        </Carousel.Item>
      </Carousel>
    </div>
  );
}

export default Home;
