// src/components/GalleryCarousel.js
import React from 'react';
import './Gallery.css';
import { Carousel } from 'react-bootstrap';
import { useSpring, animated } from 'react-spring';
import { useInView } from 'react-intersection-observer';
import SMK from '../images/smk4.jpg';
import UNIV from '../images/upi.jpeg';

function Gallery({ itemsGallery, itemsCarousel }) {
  // Efek parallax untuk galeri
  const [galleryRef, galleryInView] = useInView({
    triggerOnce: true,
  });

  const gallerySpring = useSpring({
    opacity: galleryInView ? 1 : 0,
    transform: galleryInView ? 'translateY(0)' : 'translateY(50px)',
    config: { duration: 500 },
  });

  // Efek parallax untuk carousel
  const carouselSpring = useSpring({
    from: { opacity: 0 },
    to: { opacity: 1 },
    config: { duration: 1000 },
  });

  return (
    <div className='gallerypage'> 
        <h3>My Gallery</h3> <br/>
        <animated.div style={{ ...carouselSpring,  margin: '2px 2px',}} className='caro'>
        <Carousel>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src={SMK}
              alt="First slide"
            />
            <Carousel.Caption>
              <h3>SMKN 4 Payakumbuh</h3>
              <p>My 'home' in 2019-2022</p>
            </Carousel.Caption>
          </Carousel.Item>

          <Carousel.Item>
            <img
              className="d-block w-100"
              src={UNIV}
              alt="Second slide"
            />
            <Carousel.Caption>
              <h3>Universitas Pendidikan Indonesia</h3>
              <p>My beloved 'home' at the moment</p>
            </Carousel.Caption>
          </Carousel.Item>

      
        </Carousel>
      </animated.div>  
      <animated.div
        ref={galleryRef}
        style={{ ...gallerySpring}}
        className="galleryCard"
      >
    
        <div className="gallery-images">
          <figure>
            <img src="https://via.placeholder.com/200" alt="Project 1" />
            <figcaption>Project 1 - Description</figcaption>
          </figure>
          <figure>
            <img src="https://via.placeholder.com/200" alt="Project 2" />
            <figcaption>Project 2 - Description</figcaption>
          </figure>
          <figure>
            <img src="https://via.placeholder.com/200" alt="Project 3" />
            <figcaption>Project 3 - Description</figcaption>
          </figure>
          {/* ... Add more images as needed */}
        </div>
      </animated.div>

      
    </div>
  );
}

export default Gallery;
