// src/pages/Contact.js
import React from 'react';
import './Contact.css';

function Contact() {
  return (
    <div className="contact">
      <h2>Contact Me</h2>
      <p>Feel free to reach out to me via email at [your.email@example.com].</p>
    </div>
  );
}

export default Contact;

