// src/components/GalleryCarousel.js
import React from 'react';
import { Carousel } from 'react-bootstrap';
import { useSpring, animated } from 'react-spring';
import { useInView } from 'react-intersection-observer';

function Gallery({ itemsGallery, itemsCarousel }) {
  // Efek parallax untuk galeri
  const [galleryRef, galleryInView] = useInView({
    triggerOnce: true,
  });

  const gallerySpring = useSpring({
    opacity: galleryInView ? 1 : 0,
    transform: galleryInView ? 'translateY(0)' : 'translateY(50px)',
    config: { duration: 500 },
  });

  // Efek parallax untuk carousel
  const carouselSpring = useSpring({
    from: { opacity: 0 },
    to: { opacity: 1 },
    config: { duration: 1000 },
  });

  return (
    <div>
      <animated.div
        className="gallery"
        style={{ ...gallerySpring, margin: '20px 0' }}
        ref={galleryRef}
      >
        <h3>My Gallery</h3>
        <div className="gallery-images">
          {itemsGallery.map((item, index) => (
            <figure key={index}>
              <img src={item.image} alt={item.alt} />
              <figcaption>{item.caption}</figcaption>
            </figure>
          ))}
        </div>
      </animated.div>

      <animated.div style={{ ...carouselSpring, margin: '2px 2px' }} className='caro'>
        <Carousel>
          {itemsCarousel.map((item, index) => (
            <Carousel.Item key={index}>
              <img
                className="d-block w-100"
                src={item.image}
                alt={item.alt}
              />
              <Carousel.Caption>
                <h3>{item.title}</h3>
                <p>{item.description}</p>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
        </Carousel>
      </animated.div>
    </div>
  );
}

export default Gallery;
