// src/pages/About.js
import React from 'react';
import './About.css';
import { Card, ListGroup } from 'react-bootstrap';
import { useSpring, animated } from 'react-spring';
import { useInView } from 'react-intersection-observer';

function About() {
  // Animasi untuk bagian foto dan deskripsi
  const [containerRef, containerInView] = useInView({
    triggerOnce: true,
  });

  const containerSpring = useSpring({
    opacity: containerInView ? 1 : 0,
    transform: containerInView ? 'translateY(0)' : 'translateY(50px)',
    config: { duration: 500 },
  });

  return (
    <animated.div
      className="about-container"
      style={{
        ...containerSpring,
        backgroundColor: containerInView ? '#f0f0f0' : 'transparent',
        margin: containerInView ? '20px 0' : '0',
      }}
      ref={containerRef}
    >
      <div className="about-left">
        <img src="https://via.placeholder.com/200" alt="Profile" />
      </div>
      <div className="about-right">
        <h2>About Me</h2>
        <p>
          Hi, I'm Azkal Azkiya Alfiandri. I am a passionate web developer with a focus on creating user-friendly and responsive websites.
        </p>
        <h3>Skills</h3>
        <ul>
          <li>Web Development</li>
          <li>React.js</li>
          <li>HTML/CSS</li>
        </ul>
        <h3>Experience</h3>
        <p>
          I have worked on various projects, ranging from small business websites to complex web applications.
        </p>
        <h3>Education</h3>
        <p>
          Bachelor of Science in Computer Science, [Your University]
        </p>

        {/* Projects */}
        <Card className="mt-4">
          <Card.Header>
            <h3>Projects</h3>
          </Card.Header>
          <ListGroup variant="flush">
            <ListGroup.Item>Project 1 - Description</ListGroup.Item>
            <ListGroup.Item>Project 2 - Description</ListGroup.Item>
            {/* Add more projects as needed */}
          </ListGroup>
        </Card>

        {/* Education */}
        <Card className="mt-4">
          <Card.Header>
            <h3>Education</h3>
          </Card.Header>
          <ListGroup variant="flush">
            <ListGroup.Item>[Your Degree] - [Your University]</ListGroup.Item>
            {/* Add more education details as needed */}
          </ListGroup>
        </Card>

        {/* Contact */}
        <Card className="mt-4">
          <Card.Header>
            <h3>Contact</h3>
          </Card.Header>
          <ListGroup variant="flush">
            <ListGroup.Item>Email: your.email@example.com</ListGroup.Item>
            <ListGroup.Item>LinkedIn: linkedin.com/in/your-profile</ListGroup.Item>
            {/* Add more contact details as needed */}
          </ListGroup>
        </Card>
      </div>
    </animated.div>
  );
}

export default About;
