// src/pages/Home.js
import React from 'react';
import './Home.css';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import { Carousel } from 'react-bootstrap';
import Typed from 'react-typed'; // Import react-typed library
import { useSpring, animated } from 'react-spring';
import { useInView } from 'react-intersection-observer';
import SMK from '../images/smk4.jpg';
import UNIV from '../images/upi.jpeg';

function Home() {
  // Efek parallax untuk galeri dan deskripsi
  const [galleryRef, galleryInView] = useInView({
    triggerOnce: true,
  });

  const [descriptionRef, descriptionInView] = useInView({
    triggerOnce: true,
  });

  const gallerySpring = useSpring({
    from: { opacity: 0, transform: 'translateY(100px)' },
    to: { opacity: 1, transform: 'translateY(0)' },
    config: { duration: 1700 },
  });

  const descriptionSpring = useSpring({
    from: { opacity: 0, transform: 'translateY(100px)' },
    to: { opacity: 1, transform: 'translateY(0)' },
    config: { duration: 1700 },
  });

  const carouselSpring = useSpring({
    from: { opacity: 0 },
    to: { opacity: 1 },
    config: { duration: 1000 },
  });

  return (
    <div className="page">
      {/* Komponen Typed dengan animasi teks yang diketik */}
      <Typed
        strings={['Welcome to My Portfolio!', 'I Am Azkal Azkiya Alfiandri', 'Connect with me.']}
        typeSpeed={30}
        backSpeed={30}
        backDelay={1500}
        showCursor
        cursorChar="|"
        className="typed-text"
        style={{
          display: 'block', padding: '5rem', backgroundImage: 'linear-gradient(to bottom, #4F6F52, #92ad91)', 
          margin: '0', fontSize:'50px', color: '#ECE3CE',
        }}
      />

    <animated.div
        ref={descriptionRef}
        style={{ ...descriptionSpring,  margin: '20px 0' }}
        className="description"
      >
        <p>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
        </p>
        <p>
          Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
        </p>
      </animated.div>

      <animated.div
        ref={galleryRef}
        style={{ ...gallerySpring, margin: '20px 0' }}
        className="gallery"
      >
        <h3>My Portfolio</h3>
        <div className="gallery-images">
          <img src="https://via.placeholder.com/200" alt="Project 1" />
          <img src="https://via.placeholder.com/200" alt="Project 2" />
          <img src="https://via.placeholder.com/200" alt="Project 3" />
          {/* ... Add more images as needed */}
        </div>
      </animated.div>

      <animated.div style={{ ...carouselSpring,  margin: '2px 0',}}>
        <Carousel>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src={SMK}
              alt="First slide"
            />
            <Carousel.Caption>
              <h3>First Slide</h3>
              <p>Some text about the first slide.</p>
            </Carousel.Caption>
          </Carousel.Item>

          <Carousel.Item>
            <img
              className="d-block w-100"
              src={UNIV}
              alt="Second slide"
            />
            <Carousel.Caption>
              <h3>Second Slide</h3>
              <p>Some text about the second slide.</p>
            </Carousel.Caption>
          </Carousel.Item>

      
        </Carousel>
      </animated.div>

    </div>
  );
}

export default Home;
