// src/pages/About.js
import React from 'react';
import './About.css';
import { Card, ListGroup } from 'react-bootstrap';
import { useSpring, animated } from 'react-spring';
import { useInView } from 'react-intersection-observer';
import Profil from '../images/pict1.jpg'
function About() {
  // Animasi untuk bagian foto dan deskripsi
  const [containerRef, containerInView] = useInView({
    triggerOnce: true,
  });

  const containerSpring = useSpring({
    opacity: containerInView ? 1 : 0,
    transform: containerInView ? 'translateY(0)' : 'translateY(50px)',
    config: { duration: 500 },
  });

  return (
    <animated.div
      className="about-container"
      style={containerSpring}
      ref={containerRef}
    >
      <div className="about-left">
        <img
          src={Profil}
          alt="Profile"
          style={{ borderRadius: '50%', boxShadow: '0 0 10px rgba(0, 0, 0, 0.2)'}} // Adding border-radius and shadow to the profile image
        />
      </div>
      <div className="about-right" style={{ marginLeft: '10px' }}> {/* Adding margin to separate the image and text */}
        <h2 style={{ color: '#333' }}>About Me</h2> {/* Changing text color */}
        <p>
          Hi, I'm Azkal Azkiya Alfiandri. I am a passionate web developer with a focus on creating user-friendly and responsive websites.
        </p>
        <h3 style={{ color: '#333' }}>Skills</h3>
        <ul>
          <li>Web Development</li>
          <li>React.js</li>
          <li>HTML/CSS</li>
        </ul>
        <h3 style={{ color: '#333' }}>Experience</h3>
        <p>
          I have worked on various projects, ranging from small business websites to complex web applications.
        </p>
        <h3 style={{ color: '#333' }}>Education</h3>
        <p>
          Bachelor of Science in Computer Science, [Your University]
        </p>

        {/* Projects */}
        <Card className="mt-4" style={{ borderColor: '#333' }}>
          <Card.Header style={{ backgroundColor: '#333', color: '#fff' }}> {/* Changing header background and text color */}
            <h3>Projects</h3>
          </Card.Header>
          <ListGroup variant="flush">
            <ListGroup.Item>Project 1 - Description</ListGroup.Item>
            <ListGroup.Item>Project 2 - Description</ListGroup.Item>
            {/* Add more projects as needed */}
          </ListGroup>
        </Card>

        {/* Education */}
        <Card className="mt-4" style={{ borderColor: '#333' }}>
          <Card.Header style={{ backgroundColor: '#333', color: '#fff' }}> {/* Changing header background and text color */}
            <h3>Education</h3>
          </Card.Header>
          <ListGroup variant="flush">
            <ListGroup.Item>[Your Degree] - [Your University]</ListGroup.Item>
            {/* Add more education details as needed */}
          </ListGroup>
        </Card>

      </div>
    </animated.div>
  );
}

export default About;
